record.field:
for offer in records:
    if offer.x_status != 'refused':
        offer.write({'x_status': 'refused'})

        # Check if this property has any remaining accepted offers
        remaining_accepted = offer.x_property_id.x_offer_ids.filtered(
            lambda r: r.x_status == 'accepted'
        )

        if not remaining_accepted:
            # If no accepted offers remain, reset property selling details
            new_state = 'offer_received' if offer.x_property_id.x_offer_ids else 'new'

            offer.x_property_id.write({
                'selling_price': 0.0,
                'x_partner_id': False,
                'state': new_state,
            })
